import React from 'react';
import { Link } from 'react-router-dom';

const ExerciseCard = ({ exercise }) => (
  <Link
    to={`/exercise/${exercise.id}`}
    className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 w-full max-w-[580px] mx-auto overflow-hidden hover:scale-[1.02]"
  >
    <img
      src={exercise.gifUrl}
      alt={exercise.name}
      loading="lazy"
      className="w-full h-[300px] object-cover rounded-t-2xl"
    />

    <div className="p-5 flex flex-col gap-3">
      <div className="flex flex-wrap gap-3">
        <span className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white text-xs font-semibold px-4 py-1 rounded-full capitalize">
          {exercise.bodyPart}
        </span>
        <span className="bg-gradient-to-r from-pink-400 to-red-500 text-white text-xs font-semibold px-4 py-1 rounded-full capitalize">
          {exercise.target}
        </span>
      </div>

      <h3 className="text-lg lg:text-xl font-bold text-gray-800 capitalize mt-1">
        {exercise.name}
      </h3>
    </div>
  </Link>
);

export default ExerciseCard;
