import React from 'react';
import { Link } from 'react-router-dom';

const ExerciseCard = ({ exercise }) => (
  <Link
    to={`/exercise/${exercise.id}`}
    className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-transform duration-300 transform hover:scale-[1.02] w-full max-w-[500px] mx-auto overflow-hidden"
  >
    <div className="p-6 text-center">
      <h2 className="text-[28px] font-extrabold text-gray-800 capitalize mb-5">
        {exercise.name}
      </h2>

      <img
        src={exercise.gifUrl}
        alt={exercise.name}
        loading="lazy"
        className="w-full h-[300px] object-cover rounded-xl mb-6"
      />

      <div className="flex justify-center mb-4">
        <span className="px-5 py-2 bg-blue-600 text-white text-sm font-medium rounded-full capitalize">
          {exercise.target}
        </span>
      </div>

      <p className="text-base text-gray-600 leading-relaxed">
        This exercise focuses on your <span className="capitalize font-semibold">{exercise.target}</span> using <span className="capitalize font-semibold">{exercise.equipment}</span>. A great movement to enhance strength and mobility.
      </p>
    </div>
  </Link>
);

export default ExerciseCard;
