import React from 'react';
import { Link } from 'react-router-dom';

const ExerciseCard = ({ exercise }) => (
  <Link
    to={`/exercise/${exercise.id}`}
    className="bg-white rounded-xl shadow-md hover:shadow-lg transition-transform duration-300 transform hover:scale-[1.02] w-full max-w-[380px] mx-auto overflow-hidden"
  >
    <div className="p-5 text-center">
      <h2 className="text-[22px] lg:text-[26px] font-bold text-gray-800 capitalize mb-4">
        {exercise.name}
      </h2>

      <img
        src={exercise.gifUrl}
        alt={exercise.name}
        loading="lazy"
        className="w-full h-[260px] object-cover rounded-lg mx-auto mb-4"
      />

      <div className="flex justify-center mb-3">
        <span className="px-4 py-1 bg-blue-500 text-white text-xs font-semibold rounded-full capitalize">
          {exercise.target}
        </span>
      </div>

      <p className="text-sm text-gray-600">
        This exercise targets your <span className="capitalize">{exercise.target}</span> using <span className="capitalize">{exercise.equipment}</span>. It's a great way to improve strength and flexibility.
      </p>
    </div>
  </Link>
);

export default ExerciseCard;
