import React from 'react';
import { Link } from 'react-router-dom';

const ExerciseCard = ({ exercise }) => (
  <Link
    to={`/exercise/${exercise.id}`}
    className="w-full h-full bg-white border-t-[4px] border-red-600 rounded-xl no-underline flex flex-col items-center justify-start p-4 transform transition-transform duration-300 hover:scale-105"
  >
    <h3 className="text-xl lg:text-2xl font-bold text-center capitalize mb-3 text-black">
      {exercise.name}
    </h3>

    <img
      src={exercise.gifUrl}
      alt={exercise.name}
      loading="lazy"
      className="h-[250px] w-full object-cover rounded-md"
    />

    <div className="flex gap-2 mt-4 mb-2 flex-wrap justify-center">
      <span className="px-3 py-1 text-white bg-red-400 text-xs rounded-full capitalize">{exercise.bodyPart}</span>
      <span className="px-3 py-1 text-white bg-yellow-400 text-xs rounded-full capitalize">{exercise.target}</span>
    </div>

    <p className="text-sm text-gray-600 text-center px-2 break-words leading-snug mt-2">
      A great exercise for targeting your <span className="font-semibold">{exercise.target}</span> using <span className="font-semibold">{exercise.equipment}</span>.
    </p>
  </Link>
);

export default ExerciseCard;
