import React from 'react';

import BodyPartImage from '../assets/icons/body-part.png';
import TargetImage from '../assets/icons/target.png';
import EquipmentImage from '../assets/icons/equipment.png';

const Detail = ({ exerciseDetail }) => {
  const { bodyPart, gifUrl, name, target, equipment } = exerciseDetail;

  const extraDetail = [
    {
      icon: BodyPartImage,
      name: bodyPart,
    },
    {
      icon: TargetImage,
      name: target,
    },
    {
      icon: EquipmentImage,
      name: equipment,
    },
  ];

  return (
    <div className="flex flex-col lg:flex-row items-center justify-center gap-10 px-5 py-10">
      <img
        src={gifUrl}
        alt={name}
        loading="lazy"
        className="w-[320px] lg:w-[500px] rounded-xl shadow-md object-contain"
      />

      <div className="flex flex-col gap-6 max-w-xl">
        <h2 className="text-[32px] lg:text-[48px] font-black capitalize text-black">
          {name}
        </h2>

        <p className="text-[16px] lg:text-[20px] text-gray-700 leading-7">
          Exercises keep you strong. <span className="capitalize font-semibold text-black">{name}</span> is one
          of the best exercises to target your <span className="capitalize font-semibold">{target}</span>.
          It will help you improve your mood and gain energy.
        </p>

        <div className="flex flex-col gap-5">
          {extraDetail.map((item) => (
            <div key={item.name} className="flex items-center gap-5">
              <div className="w-[70px] h-[70px] lg:w-[90px] lg:h-[90px] rounded-full bg-[#FFF2DB] flex items-center justify-center">
                <img
                  src={item.icon}
                  alt={item.name}
                  className="w-[40px] h-[40px] lg:w-[50px] lg:h-[50px]"
                />
              </div>
              <p className="text-[18px] lg:text-[24px] capitalize font-medium text-gray-800">
                {item.name}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Detail;
