import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchData, exerciseOptions } from '../utils/fetchData';
import HorizontalScrollbar from './HorizontalScrollbar';

const SearchExercises = ({ setBodyPart, bodyPart }) => {
  const [search, setSearch] = useState('');
  const [bodyParts, setBodyParts] = useState([]);
  const [allExercises, setAllExercises] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchInitialData = async () => {
      const bodyPartsData = await fetchData(
        'https://exercisedb.p.rapidapi.com/exercises/bodyPartList',
        exerciseOptions
      );
      const exercisesData = await fetchData(
        'https://exercisedb.p.rapidapi.com/exercises',
        exerciseOptions
      );
      setBodyParts(['all', ...bodyPartsData]);
      setAllExercises(exercisesData);
    };

    fetchInitialData();
  }, []);

  const handleSearch = () => {
    if (search.trim() === '') return;

    const matchedExercise = allExercises.find(
      (item) => item.name.toLowerCase() === search.trim().toLowerCase()
    );

    if (matchedExercise) {
      navigate(`/exercise/${matchedExercise.id}`);
    } else {
      alert('Exercise not found. Try a different name.');
    }

    setSearch('');
  };

  return (
    <div className="flex flex-col items-center justify-center px-5 py-8">
      <h1 className="text-[30px] lg:text-[44px] font-bold text-center mb-12">
        Awesome Exercises You <br /> Should Know
      </h1>

      <div className="relative mb-12 w-full max-w-[1170px]">
        <input
          type="text"
          placeholder="Search Exercises"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
          className="w-full h-[56px] lg:h-[76px] px-4 pr-[180px] rounded-full border border-gray-300 text-lg font-semibold focus:outline-none"
        />
        <button
          onClick={handleSearch}
          className="absolute right-0 top-0 h-[56px] lg:h-[76px] w-[80px] lg:w-[173px] bg-blue-600 text-white text-sm lg:text-lg rounded-r-full hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      <div className="w-full px-5">
        <HorizontalScrollbar
          data={bodyParts}
          bodyParts
          setBodyPart={setBodyPart}
          bodyPart={bodyPart}
        />
      </div>
    </div>
  );
};

export default SearchExercises;
